import React, { Fragment } from 'react';
import './App.css';
import ListeGare from './ListeGare';
import firebase from 'firebase';
import LogIn from './LogIn';
import Form from './Form';
import { BrowserRouter, Route } from "react-router-dom";
import FormList from './FormList';
import './Nav.css';

class App extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            connectedUser: false,
            pageReady: false,
            isAdmin: false
        }
        this.logOut = this.logOut.bind(this)
    }
    render() {

        if (this.state.pageReady && this.state.connectedUser) {
            return (
                <Fragment>
                    <nav class="navbar navbar-expand-custom navbar-mainbg">
                        <a class="navbar-brand navbar-logo" href="#">Navbar</a>
                        <button class="navbar-toggler" type="button" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                            <i class="fas fa-bars text-white"></i>
                        </button>
                        <div class="collapse navbar-collapse" id="navbarSupportedContent">
                            <ul class="navbar-nav ml-auto">
                                <div class="hori-selector"><div class="left"></div><div class="right"></div></div>
                                <li class="nav-item">
                                    <a class="nav-link" href="javascript:void(0);"><i class="fas fa-tachometer-alt"></i>Dashboard</a>
                                </li>
                                <li class="nav-item active">
                                    <a class="nav-link" href="javascript:void(0);"><i class="far fa-address-book"></i>Address Book</a>
                                </li>
                                <li class="nav-item">
                                    <a class="nav-link" href="javascript:void(0);"><i class="far fa-clone"></i>Components</a>
                                </li>
                                <li class="nav-item">
                                    <a class="nav-link" href="javascript:void(0);"><i class="far fa-calendar-alt"></i>Calendar</a>
                                </li>
                                <li class="nav-item">
                                    <a class="nav-link" href="javascript:void(0);"><i class="far fa-chart-bar"></i>Charts</a>
                                </li>
                                <li class="nav-item">
                                    <a class="nav-link" href="javascript:void(0);"><i class="far fa-copy"></i>Documents</a>
                                </li>
                            </ul>
                        </div>
                    </nav>
                    <BrowserRouter>
                        <Route exact path="/" component={ListeGare} />
                        <Route path="/formList" component={() => <FormList isAdmin={this.state.isAdmin} />} />
                        <Route path="/form" component={Form} />
                    </BrowserRouter >
                </Fragment>

            );
        } else if (this.state.pageReady && !this.state.connectedUser) {
            return (
                <div className="App">
                    <h1>Equipe de ligne</h1>
                    <LogIn />
                </div>
            )
        } else {
            return (
                <div className="App">
                    <h1>Equipe de ligne</h1>
                    <p>Chargement...</p>
                </div>
            )
        }
    }
    componentDidMount() {
        let that = this
        firebase.auth().onAuthStateChanged(function (user) {
            that.setState({ connectedUser: user });
            let userDb = firebase.firestore();
            if (user) {
                userDb.collection('adminUsers').doc(user.email).get()
                    .then(function (doc) {
                        if (doc.exists && doc.data().isAdmin === true) {
                            that.setState({ isAdmin: true })
                        }
                    }).catch(function (error) {
                        console.log("Error getting document:", error);
                    });
            }
        })


        setTimeout(() => { this.setState({ pageReady: true }) }, 1000);
    }
    logOut() {
        let that = this
        firebase.auth().signOut().then(function () {
            // Sign-out successful.
            that.setState({ connectedUser: null, isAdmin: false })
        }).catch(function (error) {
            // An error happened.
        });
    }
}

export default App;
