import React from 'react';
import './App.css';
import ListeGare from './ListeGare';
import firebase from 'firebase';

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      isAuthReady: false,
      connectedUser: null,
      mail: '',
      password: ''
    }
    this.handleChangeMail = this.handleChangeMail.bind(this);
    this.handleChangePassword = this.handleChangePassword.bind(this);
    this.firebaseLogin = this.firebaseLogin.bind(this);

  }
  render() {
    if (this.state.connectedUser) {
      return (
        <div className="App">
          <h1>Equipe de ligne</h1>
          <ListeGare />
        </div>
      );
    } else {
      return (
        <div className="App">
          <div className="connexion_form">
            <p>Connexion en cours</p>
            <form>
              <label>mail: <input id="mail" type="text" name="username" autoComplete="username" value={this.state.mail} onChange={this.handleChangeMail} required></input></label>
              <label>password: <input id="password " type="password" name="password" autoComplete="current-password" value={this.state.password} onChange={this.handleChangePassword} required></input></label>
              <button type="submit" onClick={this.firebaseLogin}>Login</button>
            </form>
          </div>
        </div>
      )
    }
  }
  componentDidMount() {
    let that = this
    firebase.auth().onAuthStateChanged(function (user) {
      that.setState({ connectedUser: user, isAuthReady: true })
    })
  }
  firebaseLogin(event) {
    event.preventDefault();
    let that = this
    firebase.auth().signInWithEmailAndPassword(this.state.mail, this.state.password)
      .then(function () {
      })
      .catch(function (error) {
        // Handle Errors here.
        var errorCode = error.code;
        console.log(error.message)
        // ...
      });

  }
  handleChangeMail(event) {
    this.setState({ mail: event.target.value });
  }
  handleChangePassword(event) {
    this.setState({ password: event.target.value });
  }
}

export default App;
