  componentDidMount() {
    let that = this;
    firebase.auth().onAuthStateChanged(function (user) {
      that.setState({
        connectedUser: user
      });
      let userDb = firebase.firestore();

      if (user) {
        userDb.collection('adminUsers').doc(user.email).get().then(function (doc) {
          if (doc.exists && doc.data().isAdmin === true) {
            that.setState({
              isAdmin: true
            });
          }
        }).catch(function (error) {
          console.log("Error getting document:", error);
        });
      }
    });
    setTimeout(() => {
      this.setState({
        pageReady: true
      });
    }, 1000);
  }

  logOut() {
    let that = this;
    firebase.auth().signOut().then(function () {
      // Sign-out successful.
      that.setState({
        connectedUser: null,
        isAdmin: false
      });
    }).catch(function (error) {// An error happened.
    });
  }

