import React from 'react';
import firebase from 'firebase'

class EquipementGare extends React.Component {
    state = {
        nom: this.props.data[0],
        etat: this.props.data[1]
    }
    render() {
        if (this.state.nom !== "nom" && this.state.nom !== "code" && this.state.nom !== "ordre") {
            return (
                <div>
                    <p className={this.state.etat} onClick={this.updateFirebase()}>{this.state.nom}</p>

                </div>

            )
        } else {
            return null
        }

    }
    updateFirebase() {
        this.setState({ etat: "Ok" })
    }
}

export default EquipementGare;
