import React, { Component } from 'react';
import Train from './Train';

class ListeTrain extends Component {
    render() {
        console.log(this.state)
        return (
            null
        );
    }
    async componentDidMount() {
        let d = new Date();
        let year = this.formatDate(d.getFullYear());
        let month = this.formatDate(d.getMonth() + 1);
        let day = this.formatDate(d.getDate());
        let hour = this.formatDate(d.getHours());
        let minute = this.formatDate(d.getMinutes());
        let second = this.formatDate(d.getSeconds());

        let date = `${year}${month}${day}${hour}${minute}${second}`
        let reponse = fetch(`https://api.navitia.io/v1/coverage/fr-se/stop_areas/${this.props.data}/departures?from_datetime=${date}&key=0a6f65c0-c3be-4fe3-a8e5-9aac523d5be7`);
        let data = await reponse;
        let train = await data.json();
        this.setState({ "train": { train } })
    }
    formatDate(number) {
        if (number < 10) {
            number = "0" + number.toString();
        } else {
            number = number.toString();
        }
        return number;
    }
}

export default ListeTrain;
