import React, { Component, Fragment } from 'react';

class FormConsult extends Component {
    constructor(props) {
        super(props);
        this.state = {
            showDetails: false,
            data: props.data.data().data,
            user: props.data.data().user,
            type: props.type
        }
        this.showDetails = this.showDetails.bind(this);
    }

    render() {
        if (this.state.type === "date") {
            return (
                <div onClick={this.showDetails}>
                    <p>{this.state.data.numeroTrain}</p>
                    {this.state.showDetails ? (
                        <Fragment>
                            <div className="info-form">
                                <p>Agent :</p>
                                <p>{this.state.user}</p>
                            </div>
                            <div className="info-form">
                                <p>Clients control√©s:</p>
                                <p>{this.state.data.nombreClient}</p>
                            </div>
                            <div className="detail-controle">
                                <div className="detail-controle-elt">
                                    <p>SB controle:</p>
                                    <p>{this.state.data.sbControle}</p>
                                </div>
                            </div>
                        </Fragment>
                    ) : ("")}
                </div>

            );
        } else {
            return (
                <div>
                    <p>{this.state.date}</p>
                </div>
            )
        }

    }
    showDetails() {
        if (this.state.showDetails) {
            this.setState({ showDetails: false })
        } else {
            this.setState({ showDetails: true })
        }
    }
}

export default FormConsult;
