import React, { Fragment } from 'react';
import EquipementGare from './EquipementGare';
import ListeTrain from './ListeTrain';

class Gare extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isShown: false,
        }
        this.showDetails = this.showDetails.bind(this);
    }
    render() {
        let equipement = Object.entries(this.props.data);
        if (this.state.isShown === true) {
            return (
                <div>
                    <div id="headingOne" class="card-header">
                        <button type="button" data-toggle="collapse" data-target="#collapseOne1" aria-expanded="true" aria-controls="collapseOne" className="text-left m-0 p-0 btn btn-link btn-block">
                            <h5 class="m-0 p-0">{this.props.data.nom} mis à jour par : {this.props.data.user}</h5>
                        </button>
                    </div>
                    <div data-parent="#accordion" id="collapseOne1" aria-labelledby="headingOne" className="collapse show">
                        <div class="card-body">1. Anim pariatur cliche reprehenderit, enim eiusmod high life accusamus terry richardson ad squid. 3 wolf moon officia aute, non cupidatat skateboard dolor brunch. Food truck quinoa
                            nesciunt
                            laborum eiusmod. Brunch 3 wolf moon tempor, sunt aliqua put a bird on it squid single-origin coffee nulla assumenda shoreditch et. Nihil anim keffiyeh helvetica, craft beer labore wes anderson cred nesciunt
                            sapiente ea proident. Ad vegan excepteur butcher vice lomo. Leggings occaecat craft beer farm-to-table, raw denim aesthetic synth nesciunt you probably haven't heard of them accusamus labore sustainable
                            VHS.
                        </div>
                    </div>

                    {/*     <div className="gare_elt">
                        <p onClick={this.showDetails}>{this.props.data.nom} mis à jour par : {this.props.data.user}</p>
                        <td></td>
                    </div>
                    <div className="equipement_elt">
                        {
                            equipement.map((item) =>
                                <EquipementGare key={item} data={item} id={this.props.id} />
                            )
                        }
                    </div>
                    <ListeTrain key={this.props.data.code} data={this.props.data.code} /> */}
                </div>
            )
        } else {
            return (
                <div id="headingOne" class="card-header">
                    <button type="button" data-toggle="collapse" data-target="#collapseOne1" aria-expanded="true" aria-controls="collapseOne" class="text-left m-0 p-0 btn btn-link btn-block">
                        <h5 class="m-0 p-0">{this.props.data.nom} mis à jour par : {this.props.data.user}</h5>
                    </button>

                    {/* 
                <div className="gare_elt" onClick={this.showDetails}>
                    <p onClick={this.showDetails}>{this.props.data.nom} mis à jour par : {this.props.data.user}</p>
                </div>
            */}
                </div>
            )
        }

    }
    showDetails() {
        if (this.state.isShown === true) {
            this.setState({ isShown: false })
        } else {
            this.setState({ isShown: true })
        }

    }
}
export default Gare
