import React from 'react';
import firebase from 'firebase'

class EquipementGare extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            nom: this.props.data[0],
            etat: this.props.data[1]
        }
        this.updateFirebase = this.updateFirebase.bind(this);
    }


    render() {
        console.log(this.props)
        if (this.state.nom !== "nom" && this.state.nom !== "code" && this.state.nom !== "ordre") {
            return (
                <div>
                    <a onClick={this.updateFirebase}><p className={this.state.etat} >{this.state.nom}</p></a>

                </div>

            )
        } else {
            return null
        }

    }
    updateFirebase() {
        let db = firebase.firestore();
        let document = db.collection('gare').doc(this.props.id);
        console.log(document)
        if (this.state.etat === "Ok") {

            this.setState({ etat: "En panne" })
        } else {
            this.setState({ etat: "Ok" })
        }
        document.update({

        })
    }
}

export default EquipementGare;
