import firebase from 'firebase'

class EquipementGare extends React.Component {
    state = {}
    render() {
        const db = firebase.firestore();
        let gare = db.collection("gare").doc(this.props.id)
        console.log(gare)
        if (this.props.data[0] !== "nom" && this.props.data[0] !== "code" && this.props.data[0] !== "ordre") {
            return (
                <div>
                    <p className={this.props.data[1]} onClick={this.updateFirebase}>{this.props.data[0]}</p>
                    <p></p>
                </div>

            )
        } else {
            return null
        }

    }
}

export default EquipementGare;
