import React, { Component, Fragment } from 'react';


class Train extends Component {
    constructor(props) {
        super(props);

        this.state = {
            data: null,
        };
    }
    render() {
        if (this.state.data === null) {
            return (
                <p>Chargement des trains</p>
            )
        } else {
            return (
                <Fragment>
                    {
                        this.state.data.departures.map((item, index) =>
                            <div key={index} className='trainElt'>
                                <p>{item.display_informations.headsign}</p>
                                <p>{item.display_informations.direction}</p>
                                <p>{this.formatHour(item.stop_date_time.departure_date_time)}</p>
                            </div>
                        )
                    }
                </Fragment>
            )
        }

    }
    formatHour(data) {
        let date = data;
        let time = date.toString().slice(-6, -2);
        let exactTime = time.slice(0, 2) + ":" + time.slice(2, 4);
        return exactTime
    }

    componentDidMount() {
        let d = new Date();
        let year = this.formatDate(d.getFullYear());
        let month = this.formatDate(d.getMonth() + 1);
        let day = this.formatDate(d.getDate());
        let hour = this.formatDate(d.getHours());
        let minute = this.formatDate(d.getMinutes());
        let second = this.formatDate(d.getSeconds());

        let date = `${year}${month}${day}${hour}${minute}${second}`
        fetch(`https://api.navitia.io/v1/coverage/fr-se/stop_areas/${this.props.data}/departures?from_datetime=${date}&key=0a6f65c0-c3be-4fe3-a8e5-9aac523d5be7`)
            .then(reponse => reponse.json())
            .then(data => this.setState({ data }));
    }
    formatDate(number) {
        if (number < 10) {
            number = "0" + number.toString();
        } else {
            number = number.toString();
        }
        return number;
    }
}

export default Train;
