import React, { Component } from 'react';
import Train from './Train';

class ListeTrain extends Component {
    constructor(props) {
        super(props);

        this.state = {
            data: null,
        };
    }
    render() {
        if (this.state.data === null) {
            return (
                <p>Chargement</p>
            )
        } else {
            return (
                <Train key={this.props.data.id} data={this.props.data} />
            );
        }
    }

}

export default ListeTrain;
