import React from 'react';
import './App.css';
import ListeGare from './ListeGare';
import firebase from 'firebase';
import LogIn from './LogIn';
import { BrowserRouter, Route, Link } from "react-router-dom";
import logoMaison from './image/home.svg';
import logoDeco from './image/log-out.svg';
import logoUser from './image/user.svg';
import About from './About';

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      connectedUser: null,
      pageReady: false,
    }
    this.logOut = this.logOut.bind(this)
  }
  render() {
    console.log(firebase.auth().getUsers)
    if (this.state.pageReady && this.state.connectedUser) {
      return (
        <div className="App">
          <h1>Equipe de ligne</h1>


          <BrowserRouter>
            <div>
              <ul className="navibar">
                <li className="navibar_lien">
                  <Link to="/"><img id="logo_maison" src={logoMaison} alt="Logo" /></Link>
                </li>
                <li className="navibar_lien">
                  <Link to="/about"><img id="logo_maison" src={logoUser} alt="Logo" /></Link>
                </li>
                <li className="navibar_lien">
                  <button id="deco_button" onClick={this.logOut}><img id="logo_deco" src={logoDeco} alt="Logo" /></button>
                </li>
              </ul>

              <hr />
              <div className="main-route-place">
                <Route exact path="/" component={ListeGare} />
                <Route path="/about" component={About} />
              </div>
            </div>
          </BrowserRouter>
        </div>
      );
    } else if (this.state.pageReady && !this.state.connectedUser) {
      return (
        <div className="App">
          <h1>Equipe de ligne</h1>
          <LogIn />
        </div>
      )
    } else {
      return (
        <div className="App">
          <h1>Equipe de ligne</h1>
          <p>Chargement...</p>
        </div>
      )
    }
  }
  componentDidMount() {
    let that = this
    firebase.auth().onAuthStateChanged(function (user) {
      that.setState({ connectedUser: user })
    })
    setTimeout(() => { this.setState({ pageReady: true }) }, 1000);
  }
  logOut() {
    let that = this
    firebase.auth().signOut().then(function () {
      // Sign-out successful.
      that.setState({ connectedUser: null })
    }).catch(function (error) {
      // An error happened.
    });
  }
}

export default App;
