import React, { Component, Fragment } from 'react';
import LogInElt from './LogInElt'

class LogIn extends Component {
    constructor(props) {
        super(props)
        this.state = {
            typeOf: "SignIn",
        }
        this.changeStateIn = this.changeStateIn.bind(this);
        this.changeStateUp = this.changeStateUp.bind(this);
    }

    render() {
        return (
            <Fragment>
                <ul className="log_choice">
                    <li ><a onClick={this.changeStateIn}>Se connecter</a></li>
                    <li><a onClick={this.changeStateUp}>Cr√©er un compte</a></li>
                </ul>
                <LogInElt typeOf={this.state.typeOf} />
            </Fragment>

        )

    }
    changeStateIn() {
        this.setState({ typeOf: "SignIn" })
    }
    changeStateUp() {
        this.setState({ typeOf: "SignUp" })
    }
}

export default LogIn;
