import React from 'react';
import './App.css';
import ListeGare from './ListeGare';
import firebase from 'firebase';
import LogIn from './LogIn';
import { BrowserRouter, Route, Link } from "react-router-dom";
import Home from './Home';
import About from './About'

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      connectedUser: null,
      pageReady: false,
    }
    this.logOut = this.logOut.bind(this)
  }
  render() {
    if (this.state.pageReady && this.state.connectedUser) {
      return (
        <div className="App">
          <h1>Equipe de ligne</h1>
          <button onClick={this.logOut}>Se deconnecter</button>
          <BrowserRouter>
            <div>
              <ul>
                <li>
                  <Link to="/">Home</Link>
                </li>
                <li>
                  <Link to="/about">About</Link>
                </li>
              </ul>

              <hr />
              <div className="main-route-place">
                <Route exact path="/" component={ListeGare} />
                <Route path="/about" component={About} />
              </div>
            </div>
          </BrowserRouter>
        </div>
      );
    } else if (this.state.pageReady && !this.state.connectedUser) {
      return (
        <div className="App">
          <h1>Equipe de ligne</h1>
          <LogIn />
        </div>
      )
    } else {
      return (
        <div className="App">
          <h1>Equipe de ligne</h1>
          <p>Chargement...</p>
        </div>
      )
    }
  }
  componentDidMount() {
    let that = this
    firebase.auth().onAuthStateChanged(function (user) {
      that.setState({ connectedUser: user })
    })
    setTimeout(() => { this.setState({ pageReady: true }) }, 1000);
  }
  logOut() {
    console.log('se deconnecter')
    firebase.auth().signOut().then(function () {
      // Sign-out successful.
    }).catch(function (error) {
      // An error happened.
    });
  }
}

export default App;
