import React, { Component, Fragment } from 'react';
import firebase from 'firebase';
import FormConsult from './FormConsult'

class FormList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            isAdmin: props.isAdmin
        }
    }
    render() {
        if (!this.state.items) {
            return (
                <form onSubmit={this.getRapportByDate(this.state.date)}>
                    <div>
                        <label htmlFor="date">Date : </label>
                        <input type="date" id="date" name="date"
                            onChange={e => this.setState({ date: e.target.value })} required></input>
                        <button className="pink_button" type="submit">Rapports</button>
                    </div>

                </form>
            )
        } else {
            return (
                <Fragment>
                    {
                        this.state.items.map((item, index) =>
                            <FormConsult data={item} key={index} type="date" />
                        )
                    }
                </Fragment>
            )
        }
    }

    /*  componentDidMount() {
          let db = firebase.firestore();
          db.collection("rapports")
  
              .onSnapshot((querySnapshot) => {
                  this.setState({
                      items: querySnapshot.docs
                  });
              })
      }*/
    getRapportByDate(date) {
        let db = firebase.firestore();
        db.collection("rapports").where("date", "==", date)

            .onSnapshot((querySnapshot) => {
                this.setState({
                    items: querySnapshot.docs
                });
            })
    }
    getRapportsByNumber(number) {
        let db = firebase.firestore();
        db.collection("rapports").where("numeroTrain", "==", number)

            .onSnapshot((querySnapshot) => {
                this.setState({
                    items: querySnapshot.docs
                });
            })
    }
}

export default FormList;
