import React from 'react';
import './App.css';
import ListeGare from './ListeGare';
import firebase from 'firebase';

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      isLoggedIn: true,
    }
  }
  render() {
    if (firebase.auth().currentUser !== null) {
      return (
        <div className="App">
          <h1>Equipe de ligne</h1>
          <ListeGare />
        </div>
      );
    } else {
      return (
        <div>
          <p>Connexion en cours</p>
          <form>
            <label>mail: <input id="mail" type="text" name="username" autocomplete="username" required></input></label>
            <label>password: <input id="password " type="password" name="password" autocomplete="current-password" required></input></label>
            <button type="submit" value="Valider">Login</button>
          </form>
        </div>
      )
    }
  }
  firebaseLogin(email, password) {
    {/*} let email = document.getElementById("mail").value;
  let password = document.getElementById("password").value;*/}
    firebase.auth().signInWithEmailAndPassword(email, password).catch(function (error) {
      // Handle Errors here.
      var errorCode = error.code;
      var errorMessage = error.message;
      // ...
    });
  }
}

export default App;
